const lobby = (state = {}, action) => {
  switch (action.type) {
    case 'SET_LOBBY_ID':
      return {
        ...state,
        id: action.lobbyId,
        players: {}
      };
    case 'SET_LOBBY':
      let amountOfPlayersVoted = {}

      for (let playerId in action.lobby.players) {
        let targetPlayerId = action.lobby.players[playerId].targetPlayerId
        if (!(targetPlayerId)) {
          continue;
        }

        if (targetPlayerId in amountOfPlayersVoted) {
          amountOfPlayersVoted[targetPlayerId]++;
        } else {
          amountOfPlayersVoted[targetPlayerId] = 1;
        }
      }

      for (let playerId in amountOfPlayersVoted) {
        action.lobby.players[playerId].amountOfReceivedVotes = amountOfPlayersVoted[playerId];
      }

      return {
        ...state,
        ...action.lobby
      };
    case 'REDUCE_LOBBY_GAME_TIME_LEFT':
      return {
        ...state,
        game: {
          ...state.game,
          timeLeft: (state.game.timeLeft) ? state.game.timeLeft - action.amount : state.game.timeLeft
        }
      };
    default:
      return state;
  }
};

export default lobby;
