import React, { useCallback, useEffect, useState } from 'react';
import { connect } from 'react-redux';

import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import ListGroup from 'react-bootstrap/ListGroup';
import Row from 'react-bootstrap/Row';

import Chat from './Chat';

import { setLobby } from '../actions';

const Lobby = ({ socket, lobby, dispatch }) => {
  const [playerReady, setPlayerReady] = useState(false);

  const setPlayerStatus = () => {
    setPlayerReady(true);
    console.log(playerReady);
    socket.emit('lobby', lobby.id, 'PLAYER_READY', { status: true });
  };

  const fetchLobby = useCallback(() => {
    socket.emit('lobbyStatus', lobby.id, (message) => {
      if ('error' in message) {
        console.log(message.error);
      } else {
        dispatch(setLobby(message));
      }
    });

    socket.on('lobbyStatus', (message) => {
      dispatch(setLobby(message));
    });
  }, [socket, dispatch, lobby.id]);

  useEffect(() => {
    fetchLobby();
  }, [fetchLobby]);

  return (
    <Container>
      <Row>
        <Col>
          <h1>Lobby {lobby.id}</h1>
          <h3> Host: {lobby.hostId}</h3>

          <ListGroup>
            {
              Object.keys(lobby.players).map((key, index) => (
                <ListGroup.Item key={index}>{lobby.players[key].alias}</ListGroup.Item>
              ))
            }
          </ListGroup>
        </Col>
      </Row>

      <Row>
        <Button variant="primary" onClick={() => setPlayerStatus(true)} disabled={playerReady}>Ready</Button>
      </Row>

      <Row>
        <Chat />
      </Row>
    </Container>
  );
}

const mapStateToProps = (state) => ({
  socket: state.socket,
  lobby: state.lobby
})

export default connect(mapStateToProps)(Lobby);
