import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';

import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import ListGroup from 'react-bootstrap/ListGroup';
import Row from 'react-bootstrap/Row';

const Chat = ({ socket, lobby, dispatch }) => {
  const [chat, setChat] = useState([]);
  const [chatMessageInput, setChatMessageInput] = useState('');

  useEffect(() => {
    socket.on('chat', (message) => {
      setChat([...chat, message.chat]);
    });
  });

  const sendChatMessage = e => {
    socket.emit('chat', lobby.id, chatMessageInput, function (data) {
      setChatMessageInput('');
    });

    e.preventDefault();
  }

  return (
    <Container fluid>
      <Row>
        <Col>
          <ListGroup>
            {chat.map((item, index) => (
              <ListGroup.Item key={index}>{item}</ListGroup.Item>
            ))}
          </ListGroup>
        </Col>
      </Row>

      <Row>
        <Col>
          <Form onSubmit={sendChatMessage}>
            <Form.Group controlId="chatMessage">
              <Form.Control value={chatMessageInput} onChange={e => setChatMessageInput(e.target.value)} type="text" required />
            </Form.Group>

            <Button variant="primary" type="submit">Enter</Button>
          </Form>
        </Col>
      </Row> 
    </Container>
  );
}

const mapStateToProps = (state) => ({
  socket: state.socket,
  lobby: state.lobby
})

export default connect(mapStateToProps)(Chat);
