import React, { useEffect } from 'react';
import { connect } from 'react-redux';

import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import ListGroup from 'react-bootstrap/ListGroup';
import Row from 'react-bootstrap/Row';

import { setLobby } from '../actions';

const Lobby = ({ socket, lobby, dispatch }) => {
  useEffect(() => {
    socket.emit('lobbyStatus', { lobbyId: lobby.id }, function (data) {
      if ('error' in data) {
        console.log(data.error);
      } else {
        dispatch(setLobby(data));
      }
    });
  }, [dispatch, lobby.id, socket]);

  if (lobby.playerIds) {
    const playerIds = lobby.playerIds.map((item, index) => (
      <ListGroup.Item>{item}</ListGroup.Item>
    ));
  } else {
    const playerIds = null;
  }

  return (
    <Container>
      <Row>
        <Col>
          <h1>Lobby {lobby.id}</h1>
          <h3> Host: {lobby.hostId}</h3>

          <ListGroup>
            {playerIds}
          </ListGroup>
        </Col>
      </Row>
    </Container>
  );
}

const mapStateToProps = (state) => ({
  socket: state.socket,
  lobby: state.lobby
})

export default connect(mapStateToProps)(Lobby);
