import React, { useCallback, useEffect } from 'react';
import { connect } from 'react-redux';

import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import ListGroup from 'react-bootstrap/ListGroup';
import Row from 'react-bootstrap/Row';

import Chat from './Chat';

import { setLobby } from '../actions';

const Game = ({ socket, lobby, dispatch }) => {
  const fetchLobby = useCallback(() => {
    socket.emit('lobbyStatus', lobby.id, (message) => {
      if ('error' in message) {
        console.log(message.error);
      } else {
        dispatch(setLobby(message));
      }
    });

    socket.on('lobbyStatus', (message) => {
      dispatch(setLobby(message));
    });
  }, [socket, dispatch, lobby.id]);

  const pickPlayer = (playerId, action) => {
    socket.emit('game', lobby.id, action, { playerId: playerId });
  };

  useEffect(() => {
    fetchLobby();
  }, [fetchLobby]);

  return (
    <Container>
      <Row>
        <Col>
          <h1>Game {lobby.id}</h1>
          <h3>Host: {lobby.hostId}</h3>
          <h3>Time: {lobby.round % 2 === 0 ? 'day time': 'night time'}</h3>

          <ListGroup>
            {
              Object.keys(lobby.players).map((id, index) => (
                <ListGroup.Item key={index} active={lobby.players[socket.id].target === id ? true : false}>
                  {lobby.players[id].alias} {socket.id === id ? <Button variant="danger" onClick={() => pickPlayer(id)}>X</Button> : ''}
                </ListGroup.Item>
              ))
            }
          </ListGroup>
        </Col>
      </Row>

      <Row>
        <Chat />
      </Row>
    </Container>
  );
}

const mapStateToProps = (state) => ({
  socket: state.socket,
  lobby: state.lobby
})

export default connect(mapStateToProps)(Game);
