import React from 'react';

import io from 'socket.io-client';

import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';

class JoinLobby extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      lobby_id: '',
      socket: io('http://127.0.0.1:3000')
    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({lobby_id: event.target.value});
  }

  handleSubmit(event) {
    console.log(this.state.lobby_id);
    event.preventDefault();

    this.state.socket.emit('join_lobby', this.state.lobby_id);
  }

  render() {
    return (
      <Container>
        <Row>
          <Col>
            <h1>JoinLobby</h1>
          </Col>
        </Row>

        <Row>
          <Col>
            <Form onSubmit={this.handleSubmit}>
              <Form.Group controlId="formLobbyId">
                <Form.Label>Lobby ID</Form.Label>
                <Form.Control value={this.state.lobby_id} onChange={this.handleChange} type="text" required placeholder="Enter Lobby ID" />
              </Form.Group>

              <Button variant="primary" type="submit">
                Submit
              </Button>
            </Form>
          </Col>
        </Row>
      </Container>
    )
  }
}

export default JoinLobby
