import React from 'react';

import { Redirect } from 'react-router-dom';

import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';

class JoinLobby extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: '',
      lobby_id: ''
    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({lobby_id: event.target.value});
  }

  handleSubmit(event) {
    console.log(this.state.lobby_id);
    event.preventDefault();

    fetch('http://192.168.178.195:3000/join-lobby', {
      method: 'POST',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        lobby_id: this.state.lobby_id
      })
    })
      .then(response => response.json())
      .then(data => {
        this.setState({
          redirect: data.lobby_id
        });
      });
  }

  render() {
    if (this.state.redirect) {
      return <Redirect to={'/lobby/' + this.state.redirect} />
    }

    return (
      <Container>
        <Row>
          <Col>
            <h1>JoinLobby</h1>
          </Col>
        </Row>

        <Row>
          <Col>
            <Form onSubmit={this.handleSubmit}>
              <Form.Group controlId="formLobbyId">
                <Form.Label>Lobby ID</Form.Label>
                <Form.Control value={this.state.lobby_id} onChange={this.handleChange} type="text" required placeholder="Enter Lobby ID" />
              </Form.Group>

              <Button variant="primary" type="submit">
                Submit
              </Button>
            </Form>
          </Col>
        </Row>
      </Container>
    )
  }
}

export default JoinLobby
