import React, { useEffect } from 'react';
import { connect } from 'react-redux';


import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import ListGroup from 'react-bootstrap/ListGroup';
import Row from 'react-bootstrap/Row';


import Chat from './Chat';

import { setLobby } from '../actions';

const Lobby = ({ socket, lobby, dispatch }) => {
  useEffect(() => {
    socket.emit('lobbyStatus', { lobbyId: lobby.id }, function (data) {
      if ('error' in data) {
        console.log(data.error);
      } else {
        dispatch(setLobby(data));
      }
    });
  }, [dispatch, lobby.id, socket]);

  return (
    <Container>
      <Row>
        <Col>
          <h1>Lobby {lobby.id}</h1>
          <h3> Host: {lobby.hostId}</h3>

          <ListGroup>
            {lobby.playerIds.map((item, index) => (
              <ListGroup.Item key={index}>{item}</ListGroup.Item>
            ))}
          </ListGroup>
        </Col>
      </Row>

      <Row>
        <Chat />
      </Row>
    </Container>
  );
}

const mapStateToProps = (state) => ({
  socket: state.socket,
  lobby: state.lobby
})

export default connect(mapStateToProps)(Lobby);
