const lobby = (state = {}, action) => {
  switch (action.type) {
    case 'SET_LOBBY_ID':
      return {
        ...state,
        id: action.lobbyId,
        players: {}
      };
    case 'SET_LOBBY':
      return action.lobby;
    case '':
      return {
        ...state,
        game: {
          timeLeft: action.timeLeft
        }
      };
    default:
      return state;
  }
}

export default lobby;
