import React, { useCallback, useEffect, useState } from 'react';
import { connect } from 'react-redux';

import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import ListGroup from 'react-bootstrap/ListGroup';
import Row from 'react-bootstrap/Row';

import Chat from './Chat';


const Game = ({ socket, lobby, dispatch }) => {
  const [playerReady, setPlayerReady] = useState(false);

  const pickPlayer = (playerId) => {

  };

  const fetchLobby = useCallback(() => {
    socket.emit('lobbyStatus', lobby.id, (message) => {
      if ('error' in message) {
        console.log(message.error);
      } else {
        dispatch(setLobby(message));
      }
    });

    socket.on('lobbyStatus', (message) => {
      dispatch(setLobby(message));
    });
  }, [socket, dispatch, lobby.id]);

  useEffect(() => {
    fetchLobby();
  }, [fetchLobby]);

  return (
    <Container>
      <Row>
        <Col>
          <ListGroup>
            {
              Object.keys(lobby.players).map((id, index) => (
                <ListGroup.Item key={index} variant={lobby.players[id].status === 'DEAD' ? 'danger' : ''} >
                  {lobby.players[id].alias}
                </ListGroup.Item>
              ))
            }
          </ListGroup>
        </Col>
      </Row>

      <Row>
        <Button variant={playerReady ? 'danger' : 'primary'} onClick={() => setPlayerStatus(!(playerReady))}>{playerReady ? 'Not ready' : 'Ready'}</Button>
      </Row>

      <Row>
        <Chat />
      </Row>
    </Container>
  );
}

const mapStateToProps = (state) => ({
  socket: state.socket,
  lobby: state.lobby
})

export default connect(mapStateToProps)(Lobby);
