import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { connect } from 'react-redux';

import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';

import { setLobbyId } from '../actions';

const CreateLobby = ({ dispatch }) => {
  const history = useHistory();
  const [isLoading, setLoading] = useState(false);

  function createLobby() {
    setLoading(true);

    props.socket.emit('create_lobby', '', function (data) {
      dispatch(setLobbyId(data.lobby_id)); 
      history.push(`/lobby/${data.lobby_id}`);
    });
  }

  return (
    <Container>
      <Row>
        <Col>
          <h1>CreateLobby</h1>
        </Col>
      </Row>

      <Row>
        <Button variant="primary" disabled={isLoading} onClick={!isLoading ? createLobby : null}>{isLoading ? 'Loading...' : 'Create Lobby'}</Button>
      </Row>
    </Container>
  );
}

export default connect()(CreateLobby)
