import React from 'react';
import { connect } from 'react-reduc';

const Chat = ({ socket }) => {
  const [chat, setChat] = useState([]);
  const [chatMessage, setChatMessage] = useState('');

  useEffect(() => {
    socket.on('chat', (message) => {
      setChat([...chat, message.chat]);
    });
  });

  function sendChatMessage() {
    socket.emit('chat', lobby.id, chatMessage, function (data) {
      setChatMessage('');
    });
  }

  return (
    <ContainerFluid>
      <Row>
        <Col>
          <ItemGroup>
            {chat.map((item, index) => (
              <ListGroup.Item key={index}>{item}</ListGroup.Item>
            ))}
          </ItemGroup>
        </Col>
      </Row>

      <Row>
        <Col>
          <Form onSubmit={sendChat}>
            <Form.Group controlId="chatMessage">
              <Form.Control value={chatMessageInput} onChange={e => setChatMessageInput(e.target.value)} type="text" required />
            </Form.Group>

            <Button variant="primary" type="submit" onClick={sendChatMessage}>Enter</Button>
          </Form>
        </Col>
      </Row> 
    </ContainerFluid>
  );
}

const mapStateToProps = (state) => ({
  socket: state.socket
})

export default connect()(Chat);
