import React, { useState} from 'react';
import { useHistory } from 'react-router-dom';
import { connect } from 'react-redux';

import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';

import { setLobbyId } from '../actions';

const JoinLobby = ({ socket, dispatch }) => {
  const history = useHistory();
  const [isLoading, setLoading] = useState(false);

  const [lobbyIdInput, setLobbyIdInput] = useState('');

  function joinLobby() {
    setLoading(true);

    socket.emit('joinLobby', lobbyIdInput, function (data) {
      if ('error' in data) {
        console.log(data.error);
      } else {
        dispatch(setLobbyId(data.lobbyId));
        history.push(`/lobby/${data.lobbyId}`);
      }
    });
  }

  return (
    <Container>
      <Row>
        <Col>
          <h1>JoinLobby</h1>
        </Col>
      </Row>

      <Row>
        <Col>
          <Form onSubmit={joinLobby}>
            <Form.Group controlId="formLobbyId">
              <Form.Label>Lobby ID</Form.Label>
              <Form.Control value={lobbyIdInput} onChange={e => setLobbyIdInput(e.target.value)} type="text" required placeholder="Enter Lobby ID" />
            </Form.Group>

            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  )
}

const mapStateToProps = state => ({
  socket: state.socket
});

export default connect(mapStateToProps)(JoinLobby);
